
def add_zeros(path, matrix_l):
    zeros_to_add = matrix_l - len(path)
    zeros_list = [0 for i in range(zeros_to_add)]
    new_path = path + zeros_list
    return new_path


def input_reads(reads):
    reads = sorted(reads, key=len, reverse=True)
    L_matrix = max(len(reads), len(reads[0]))
    new_reads = []
    for i in reads:
        new_reads.append(add_zeros(i, L_matrix))
    rows_to_add = L_matrix - len(reads)
    zeros_list = [0 for i in range(L_matrix)]
    for i in range(rows_to_add):
        new_reads.append(zeros_list)
    return new_reads

